foreach ($p in $pairs) {
    $dir = Join-Path $base $p.cat
    if (-not (Test-Path $dir)) { New-Item -ItemType Directory -Path $dir -Force | Out-Null }
    $htmlPath = Join-Path $dir "screen-$($p.id).html"
    $imgPath = Join-Path $dir "screenshot-$($p.id).png"
    try {
        Invoke-WebRequest -Uri $p.html -OutFile $htmlPath -UseBasicParsing -ErrorAction Stop
        Write-Host "OK HTML $($p.cat)/$($p.id)"
    } catch { Write-Host "FAIL HTML $($p.cat)/$($p.id): $_" }
    try {
        Invoke-WebRequest -Uri $p.img -OutFile $imgPath -UseBasicParsing -ErrorAction Stop
        Write-Host "OK IMG $($p.cat)/$($p.id)"
    } catch { Write-Host "FAIL IMG $($p.cat)/$($p.id): $_" }
}
Write-Host "Done."
